import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { REMOVE } from "../redux/actions/action";
import Popover from "react-bootstrap/Popover";

const Cart = () => {
  const getData = useSelector((state) => state.cartReducer.carts);
  const dispatch = useDispatch();

  const remove = (id) => {
    dispatch(REMOVE(id));
  };

  const popover = (
    <Popover id="popover-basic">
      <table>
        <thead>
          <tr>
            <th className="me-5 text-center">Image</th>
            <th className="me-5 text-center">Item</th>
          </tr>
        </thead>
        <tbody>
          {getData.map((item, index) => (
            <tr className="ms-2" key={index}>
              <td>
                <img
                  src={item.image}
                  style={{
                    width: "5rem",
                    height: "5rem",
                    margin: "15px 15px",
                  }}
                  alt=""
                />
              </td>
              <td>
                <div className="text-center mt-3 me-3">
                  <p className="text-center mb-0 fw-bolder">{item.category}</p>
                  <p className="mb-0">Price: $ {item.price}</p>
                  <p className="mb-0">Rating: {item.rating.rate}&#x2605;</p>
                  <p className="mb-0">{item.title.substring(0, 45)}</p>
                  <p className="mt-1" onClick={() => remove(item.id)}>
                    <i
                      className="fa fa-trash fs-4 delete-icon"
                      aria-hidden="true"
                    ></i>
                  </p>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Popover>
  );

  return (
    <div>
      {/* Your Cart Component JSX */}
      {/* ... */}
      {/* Display Popover */}
      {popover}
    </div>
  );
};

export default Cart;
