import React from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import { REMOVE } from "../redux/actions/action";

const NavBarComp = () => {
  const getData = useSelector((state) => state.cartReducer.carts);

  const dispatch = useDispatch();
  const remove = (id) => {
    dispatch(REMOVE(id));
  };
  const popover = (
    <Popover id="popover-basic">{/* Ваш Popover контент */}</Popover>
  );

  return (
    <div>
      <Navbar bg="light" variant="light" className="shadow-sm bg-white py-4 ">
        <Container>
          <Link style={{ textDecoration: "none" }} to="/">
            <Navbar.Brand className="fw-bold fs-2">LOGO=)</Navbar.Brand>
          </Link>
          <Nav className="me-auto">{/* Ваші Nav.Link */}</Nav>
          <div className="buttons">
            {/* OverlayTrigger з використанням кнопки */}
            <OverlayTrigger
              trigger="click"
              placement="bottom"
              overlay={popover}
            >
              <Button variant="outline-dark" className="me-2">
                <i className="fa fa-shopping-cart me-2"></i>Cart
              </Button>
            </OverlayTrigger>
            {/* Інші кнопки */}
            <Button variant="outline-dark" className="me-2">
              <i className="fa fa-user-plus me-2"></i>Login
            </Button>
            <Button variant="outline-dark" className="me-2">
              <i className="fa fa-sign-in me-2"></i>Register
            </Button>
          </div>
        </Container>
      </Navbar>
    </div>
  );
};

export default NavBarComp;
