import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { REMOVE } from "../redux/actions/action";

const Cart = () => {
  const [itemQuantities, setItemQuantities] = useState(1);

  const getData = useSelector((state) => state.cartReducer.carts);
  const dispatch = useDispatch();

  const decrementQuantity = (itemId) => {
    const updatedCart = cartItems.map((item) =>
      item.id === itemId && item.quantity > 0
        ? { ...item, quantity: item.quantity - 1 }
        : item
    );
    setCartItems(updatedCart);
  };

  const incrementQuantity = (itemId) => {
    const updatedCart = cartItems.map((item) =>
      item.id === itemId ? { ...item, quantity: item.quantity + 1 } : item
    );
    setCartItems(updatedCart);
  };

  const removeItem = (itemId) => {
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCart);
  };

  return (
    <table>
      <thead>{/* Table Headers */}</thead>
      <tbody>
        {cartItems.map((item, index) => (
          <tr className="ms-2" key={index}>
            {/* Item Details */}
            <td>
              <div className="text-center">
                <button onClick={() => decrementQuantity(item.id)}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => incrementQuantity(item.id)}>+</button>
              </div>
            </td>
            <td>
              <div className="text-center">${item.price * item.quantity}</div>
            </td>
            <td>
              <div className="text-center" onClick={() => removeItem(item.id)}>
                <i
                  className="fa fa-trash fs-4 delete-icon"
                  aria-hidden="true"
                ></i>
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Cart;
