import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { REMOVE } from "../redux/actions/action";

const Cart = () => {
  const [itemQuantities, setItemQuantities] = useState(1);

  const getData = useSelector((state) => state.cartReducer.carts);
  const dispatch = useDispatch();

  const decrementQuantity = (itemId) => {
    setItemQuantities((prevQuantities) => ({
      ...prevQuantities,
      [itemId]: (prevQuantities[itemId] || 1) - 1,
    }));
  };

  const incrementQuantity = (itemId) => {
    setItemQuantities((prevQuantities) => ({
      ...prevQuantities,
      [itemId]: (prevQuantities[itemId] || 1) + 1,
    }));
  };

  const removeItem = (itemId) => {
    setItemQuantities((prevQuantities) => {
      const updatedQuantities = { ...prevQuantities };
      delete updatedQuantities[itemId];
      return updatedQuantities;
    });
  };

  return (
    <table>
      {/* Тут ваша розмітка заголовків таблиці */}
      <tbody>
        {getData.map((item, index) => (
          <tr className="ms-2" key={item.id}>
            <td>{/* Ваші дані товарів */}</td>
            <td>
              <div className="text-center">
                <button onClick={() => decrementQuantity(item.id)}>-</button>
                <span>{itemQuantities[item.id] || 1}</span>
                <button onClick={() => incrementQuantity(item.id)}>+</button>
              </div>
            </td>
            <td>
              <div className="text-center">
                {/* Рахунок ціни за товар з урахуванням кількості */}
              </div>
            </td>
            <td>
              <div className="text-center" onClick={() => removeItem(item.id)}>
                {/* Іконка для видалення товару */}
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Cart;
