import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { REMOVE } from "../redux/actions/action";

const Cart = () => {
  const getData = useSelector((state) => state.cartReducer.carts);
  const dispatch = useDispatch();

  const [itemQuantities, setItemQuantities] = useState(
    getData.reduce((quantities, item) => {
      quantities[item._id] = item.quantity || 1; // Використовуємо кількість із cartItems або 1, якщо вона не вказана
      return quantities;
    }, {})
  );

  const decrementQuantity = (id) => {
    if (itemQuantities === 0) {
      remove();
    } else {
      setItemQuantities(itemQuantities - 1);
    }
  };

  const incrementQuantity = (item) => {};

  const remove = (id) => {
    dispatch(REMOVE(id));
  };

  return (
    <table>
      <thead>
        <tr>
          <th className="me-5 text-center">Image</th>
          <th className="me-5 text-center">Item</th>
          <th className="me-5 text-center">Quantity</th>
          <th className="me-5 text-center">Price</th>
        </tr>
      </thead>
      <tbody>
        {getData.map((item, index) => (
          <tr className="ms-2" key={item.id}>
            <td>
              <img
                src={item.image}
                style={{
                  width: "5rem",
                  height: "5rem",
                  margin: "15px 15px",
                }}
                alt=""
              />
            </td>
            <td>
              <div className="text-center mt-3 me-3">
                <p className="text-center mb-0 fw-bolder">{item.category}</p>
                <p className="mb-0">Rating: {item.rating.rate}&#x2605;</p>
                <p className="mb-0">{item.title.substring(0, 45)}</p>
                <p className="mt-1" onClick={() => remove(item.id)}>
                  <i
                    className="fa fa-trash fs-4 delete-icon"
                    aria-hidden="true"
                  ></i>
                </p>
              </div>
            </td>
            <td>
              <div className="text-center">
                <button onClick={() => decrementQuantity()}>-</button>
                <span>{itemQuantities}</span>
                <button onClick={() => incrementQuantity()}>+</button>
              </div>
            </td>
            <td>
              <div className="text-center">${item.price * itemQuantities}</div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Cart;
